mapper.insert:

INSERT INTO additional_info (
    addinfo_id,
    fct_id,
    addinfo_desc
) VALUES (
    addinfo_pk_seq.NEXTVAL,
    #{addinfo.fctId},
    #{addinfo.addinfoDesc}
)

mapper.delete:
DELETE FROM additional_info
WHERE addinfo_id IN

        #{id}

mapper.select:
SELECT s.fct_id AS "fctId", s.fct_name AS "fctName", s.cat_id AS "catId", s.cat_name AS "catName", s.img_path AS "repImgUrl" FROM ( SELECT ROWNUM AS rnum, s.fct_id AS fct_id, s.fct_name AS fct_name, s.cat_id AS cat_id, s.cat_name AS cat_name, s.img_path AS img_path FROM ( SELECT fct.fct_id AS fct_id, fct.fct_name AS fct_name, cat.cat_id AS cat_id, cat.cat_name AS cat_name, img.img_path AS img_path FROM facility fct INNER JOIN category cat ON cat.cat_id = fct.cat_id LEFT JOIN image img ON img.img_attached_to = 'F'
fct.prov_id = #{dto.providerId}
AND fct.fct_name LIKE '%' || #{dto.searchKeyword} || '%'
AND fct.fct_latitude <= #{dto.upperLatitude} AND fct.fct_latitude >= #{dto.lowerLatitude} AND fct.fct_longitude <= #{dto.upperLongitude} AND fct.fct_longitude >= #{dto.lowerLongitude}
fct.cat_id IN
#{catId}
ORDER BY
POWER(fct.fct_latitude - #{dto.centerLatitude}, 2) + POWER(fct.fct_longitude - #{dto.centerLongitude}, 2)
fct.created_at
ASC
DESC
) s ) s WHERE s.rnum BETWEEN #{dto.page} - 1 * #{dto.pageSize} + 1 AND #{dto.page} * #{dto.pageSize} ORDER BY s.rnum

SELECT
    COUNT(*) 
FROM facility fct
INNER JOIN category cat ON cat.cat_id = fct.cat_id
LEFT JOIN image img ON img.img_attached_to = 'F'


        fct.prov_id = #{dto.providerId}



            AND fct.fct_name LIKE '%' || #{dto.searchKeyword} || '%'




            AND fct.fct_latitude <= #{dto.upperLatitude}
                AND fct.fct_latitude >= #{dto.lowerLatitude}
                AND fct.fct_longitude <= #{dto.upperLongitude}
                AND fct.fct_longitude >= #{dto.lowerLongitude}



        AND fct.cat_id IN

            #{catId}
